sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below

	if(g_lang == 'es'){ 
  g_form.setSectionDisplay('datos_decompra', true); 
  g_form.setSectionDisplay('purchase_data', false);

  g_form.setSectionDisplay('garantía', true); 
  g_form.setSectionDisplay('warranty', false);		

  g_form.setSectionDisplay('garantía_deservicio', true); 
  g_form.setSectionDisplay('service_warranty', false);

  g_form.setSectionDisplay('reventa', true); 
  g_form.setSectionDisplay('resale', false);

  g_form.setSectionDisplay('devolución_delproducto', true); 
  g_form.setSectionDisplay('returns', false);

	}

else if(g_lang == 'en'){
   g_form.setSectionDisplay('datos_decompra', false); 
  g_form.setSectionDisplay('purchase_data', true);

  g_form.setSectionDisplay('garantía', false); 
  g_form.setSectionDisplay('warranty', true);		

  g_form.setSectionDisplay('garantía_deservicio', false); 
  g_form.setSectionDisplay('service_warranty', true);

  g_form.setSectionDisplay('reventa', false); 
  g_form.setSectionDisplay('resale', true);

  g_form.setSectionDisplay('devolución_delproducto', false); 
  g_form.setSectionDisplay('returns', true);

	}	
}
